#define FIO std::ios::sync_with_stdio(false);std::cin.tie(0);
```

```cpp
void slove() {
	int B,t1,t2,a1,a2;
	cin>>B>>t1>>a1>>t2>>a2;

	int f = 0;

	if(t1 > t2){
		swap(t1,t2);swap(a1,a2);
		f=1;
	}

	double d = t2-t1;
	double a,b;

	if(d * B > a1){
		a = 1.0 *a1 / B;
		b = 1.0 *a2 / B;
		d=0;
	}else {
