int dfs(int x,int y){
	if(x==n && y == m) return 1;
	if(x >n || y> m || (!st[x][y])) return 0;
	st[x][y] = 0;

	if(dfs(x+1,y)) return 1;
	if(dfs(x,y+1)) return 1;

	return 0;
}

void slove() {
	memset(f,0x3f,sizeof f);
	cin>>n>>m;
	for(int i=1;i<=n;i++) for(int j=1;j<=m;j++)
		cin>>a[i][j];

	for(int i=1;i<=n;i++) for(int j=1;j<=m;j++)
		cin>>b[i][j];

	f[1][0] = f[0] [1] = 0; 
	for(int i = 1;i<=n;i++) for(int j=1;j<=m;j++){
		f[i][j] = min(f[i-1][j] + b[i][j], f[i][j-1] + b[i][j]);
	}

	// for(int i=1;i<=n;++i) {
	// 	for(int j=1;j<=m;++j)
	// 		cout<<f[i][j]<<'\t';
	// 	cout<<endl;
	// }

	for(int i=1;i<=n;i++) for(int j=1;j<=m;++j){
		if(f[i][j] + a[1][1] <= a[i][j]){
			st[i][j]  = 1;
		}
	}

	st[1][1] = st[n][m] = 1;

