	cin>>m;
	build(1,n,1,tr1);
	build(1,n,1,tr2);

	modify(1,c,1,tr1);
	modify(1,-c,1,tr2);

	for(int i=1;i<=m;i++){
		cin>>t[i]>>p[i];
		int l = query(1,t[i],1,tr1);
		int r = query(t[i],n,1,tr2);
		// cout<<l<<' '<<r<<endl;
		int mx = max(l+p[i]-t[i]*c,r+p[i]+t[i]*c);
		f[t[i]] = mx;
		// cout<<t[i]<< ' ' <<f[t[i]]<<nline;
		modify(t[i],f[t[i]] + t[i] * c,1,tr1);
		modify(t[i],f[t[i]] - t[i] * c,1,tr2);
	}

	int ans = 0;
	for(int i=1;i<=n;i++) ans =max(ans,f[i]);

	cout<<ans<<endl;
	return ;
}
